'use strict';require("dotenv").config();var createError=require("http-errors");var express=require("express");var path=require("path");var cookieParser=require("cookie-parser");var logger=require("morgan");var cors=require("cors");var helmet=require("helmet");var apiRouter=require("./routes/values");var app=express();app.set("views",path.join(__dirname,"views"));app.set("view engine","");app.use(helmet());app.use(logger("dev"));app.use(express.json());app.use(express.urlencoded({extended:false}));app.use(cookieParser());app.use(cors());app.use(express["static"](path.join(__dirname,"public")));app.use("/api",apiRouter);app.use(function(req,res,next){next(createError(404))});app.use(function(err,req,res,next){res.locals.message=err.message;res.locals.error=req.app.get("env")==="development"?err:{};res.status(err.status||500);res.json({message:err.message})});module.exports=app;